/// Sort `v` using a sorting network when `2 <= v.len() <= 32`.
/// Falls back to `sort_unstable()` outside that range.
#[inline(always)]
pub fn sort_net_or_std(v: &mut [u64]) {
    let n = v.len();
    if n < 2 {
        return;
    }
    if n > 32 {
        v.sort_unstable();
        return;
    }
    unsafe {
        let base = v.as_mut_ptr();
        match n {
            2 => sort2_net(base),
            3 => sort3_net(base),
            4 => sort4_net(base),
            5 => sort5_net(base),
            6 => sort6_net(base),
            7 => sort7_net(base),
            8 => sort8_net(base),
            9 => sort9_net(base),
            10 => sort10_net(base),
            11 => sort11_net(base),
            12 => sort12_net(base),
            13 => sort13_net(base),
            14 => sort14_net(base),
            15 => sort15_net(base),
            16 => sort16_net(base),
            17 => sort17_net(base),
            18 => sort18_net(base),
            19 => sort19_net(base),
            20 => sort20_net(base),
            21 => sort21_net(base),
            22 => sort22_net(base),
            23 => sort23_net(base),
            24 => sort24_net(base),
            25 => sort25_net(base),
            26 => sort26_net(base),
            27 => sort27_net(base),
            28 => sort28_net(base),
            29 => sort29_net(base),
            30 => sort30_net(base),
            31 => sort31_net(base),
            32 => sort32_net(base),
            _ => unreachable!("missing network"),
        }
    }
}
